package com.java.array;

public class Array2_ForEach {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		String name[] = {"aa","bb","cc","dd"};
		
		for(String i:name) {
			System.out.println(i);
			
		}
		
		
		
	int num[] = new int[10];
		
		num[0] = 10;
		num[1] = 20;
		num[2] =30;
		num[3]=40;
		num[4]=50;
		
	
		for(int j:num)
		{
			System.out.println(j);
		}
	
		
	}

}
