package com.java.loop;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int i=1;
		
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		
		
		int j=1;
		
		int sum = 0;
		
		while(j<=10)
		{
		sum =	sum+j;
		j++;
		}
		
		System.out.println(sum);
		
		
		
		System.out.println("********");
		
		int k=0;
		
		while(k>0)
		{
			System.out.println(k);
			k--;
		}

	}

}
